    public function XMLRulesXSLTRNGTest($rule, $odataVersion)
    {
        $response = $this->getMetadataDocument($odataVersion);

        $xml = new DOMDocument();
        $xml->loadXML($response->content());

        # START XSLT
        $xslt = new XSLTProcessor();

        $xsl = new DOMDocument();
        $xsl->loadXML(base64_decode($rule));
        $xslt->importStylesheet($xsl);
        $rng = $xslt->transformToXML($xml);
        try {
            $this->assertTrue($xml->relaxNGValidateSource($rng));
        } catch (Exception $e) {
            $this->fail($e->getMessage() . '. RelaxNG Input: ' . $rng);
        }
    }


    /**
     * @dataProvider XSLTRngRulesProvider
     */
    public function testXMLRulesXSLTRNG($rule, $odataVersions)
    {
        foreach ($odataVersions as $version) {
            if (3 != $version) {
                continue;
            }
            $this->XMLRulesXSLTRNGTest($rule, $version);
        }
    }

    public function XSLTRngRulesProvider()
    {
        return [
            'IsDefaultEntityContainer attribute MUST be used on an EntityContainer element [MC-CSDL] to indicate which EntityContainer is the default container for the data service. [2.2.3.7.2]' => ["",[1,2,3,4]],
            'The MimeType attribute MUST be used on a <Property> element [MC-CSDL] to indicate the media type of the content to be stored in the property being defined by the XML element. [2.2.3.7.2]' => ["",[1,2,3]],
            'HttpMethod attribute MUST be used on a <FunctionImport> element [MC-CSDL] to indicate the HTTP method which is to be used to invoke the ServiceOperation exposing the FunctionImport. [2.2.3.7.2]' =>["",[1,2,3]],
            'HasStream attribute MUST only be used on an <EntityType> element [MC-CSDL] that represents a Media Link Entry. [2.2.3.7.2]' => ["",[1,2,3]],
            'DataServiceVersion attribute MUST be in the data service metadata namespace and SHOULD be present on a <edmx:DataServices> element [MC-EDMX] to indicate the version of the data service CSDL annotations (attributes in the data service metadata namespace) used by the document. [2.2.3.7.2]' => ["",[1,2,3]],
            'The contents of the <edmx:Edmx> element, in the context of a data service, is an <edmx:DataServices> element which contains one or more conceptual schema definition language (CSDL) documents. [2.2.3.7.2]' =>["",[1,2,3]],
            'The valid values for IsDefaultEntityContainer attribute are "true" or "false". [2.2.3.7.2]' => ["",[1,2,3]],

        ];
    }


    public function HeadersTest($field, $Regex, $searchString, $odataVersion)
    {
        $response = $this->GetMetadataDocument($odataVersion);
        $fieldValue = $response->headers->get($field);
        if (null != $searchString) {
            $this->assertTrue(
                str_contains($fieldValue, $searchString),
                "could not locate search string: " . $searchString . " within Field: ". $field
            );
        }
        $this->assertEquals(
            1,
            preg_match($Regex, $fieldValue),
            "Field: " . $field .' had value: ' . $fieldValue . " which is not matched by regex: " . $Regex
        );
    }


    /**
     * @dataProvider RegExHeaderRulesProvider
     */
    public function testHeaders($field, $Regex, $searchString, $odataVersions)
    {
        foreach ($odataVersions as $version) {
            if (3 != $version) {
                continue;
            }
            $this->HeadersTest($field, $Regex, $searchString, $version);
        }
    }

    public function RegExHeaderRulesProvider()
    {
        return [
            'AtomPub Service Documents MUST be identified with the \'application/xml\' Content Type.' => ["Content-Type", "/.*application\/xml.*/", "application/xml" ,[1,2,3,4]],
        ];
    }
