<?php

use App\User;
use Illuminate\Foundation\Testing\WithoutMiddleware;
use Illuminate\Foundation\Testing\DatabaseMigrations;
use Illuminate\Foundation\Testing\DatabaseTransactions;
use Illuminate\Support\Facades\Route;
use Mockery as m;

class XMLServiceDocumentTest extends TestCase
{
    /**
     * A basic test example.
     *
     * @return void
     */
    public function testCheckToSeeNameSpaces()
    {
        $user = new User();

        $this->actingAs($user)->visit('/odata.svc')
             ->see('xmlns:atom="http://www.w3.org/2005/Atom"')
             ->see('xml:base="http://localhost/odata.svc/"')
             ->see('xmlns="http://www.w3.org/2007/app"');

    }

    public function getServiceDocument($version)
    {
        switch ($version) {
            case 1:
                $version = "1.0;";
                break;
            case 2:
                $version = "2.0;";
                break;
            case 3:
                $version = "3.0;";
                break;
            case 4:
                $version = "4.0;";
                $this->markTestSkipped("Odata Version 4 not implomented yet");
                break;
            default:
                $this->fail("Requested a version not between 1 and 4");
        }
        $response = $this->call(
            'GET',
            '/odata.svc',
            [],
            [],
            [],
            [ "DataServiceVersion" => $version, "MaxDataServiceVersion" => $version]
        );
        $this->assertEquals($version, $response->headers->get("DataServiceVersion"));
        return $response;
    }

    public function XMLRulesRNGTest($rule, $odataVersion)
    {
        $response = $this->getServiceDocument($odataVersion);
        $xml = new DOMDocument();
        $xml->loadXML($response->content());

        try {
            $this->assertTrue($xml->relaxNGValidateSource(base64_decode($rule)));
        } catch (Exception $e) {
            $this->fail($e->getMessage() . '. RelaxNG Input: ' . base64_decode($rule));
        }

    }


    /**
     * @dataProvider RngRulesProvider
     */
    public function testXMLRulesRNG($rule, $odataVersion)
    {
        foreach ($odataVersion as $version) {
            if (3 != $version) {
                continue;
            }

            $this->XMLRulesRNGTest($rule, $version);
        }
    }

    public function RngRulesProvider()
    {
        return [
            'The root URL of a data service that implements the AtomPub protocol SHOULD identify the service document. [2.2.6.2.7]'  => ["", [1,2,3,4]],
            'A data service SHOULD represent all available collections in a single <app:workspace> element. [2.2.6.2.7]' => ["",[1,2,3,4]],
            'The URI identifying the EntitySet MUST be used as the value of the href attribute of the <app:collection> element. [2.2.6.2.7]' => ["",[1,2,3,4]],
        ];
    }


    public function XMLRulesXSLTRNGTest($rule, $odataVersion)
    {
        $response = $this->getServiceDocument($odataVersion);

        $xml = new DOMDocument();
        $xml->loadXML($response->content());

        # START XSLT
        $xslt = new XSLTProcessor();

        $xsl = new DOMDocument();
        $xsl->loadXML(base64_decode($rule));
        $xslt->importStylesheet($xsl);
        $rng = $xslt->transformToXML($xml);
        try {
            $this->assertTrue($xml->relaxNGValidateSource($rng));
        } catch (Exception $e) {
            $this->fail($e->getMessage() . '. RelaxNG Input: ' . $rng);
        }
    }

    /**
     * @dataProvider XSLTRngRulesProvider
     */
    public function testXMLRulesXSLTRNG($rule, $odataVersion)
    {
        foreach ($odataVersion as $version) {
            if (3 != $version) {
                continue;
            }

            $this->XMLRulesXSLTRNGTest($rule, $version);
        }

    }

    public function XSLTRngRulesProvider()
    {
        return [
            'A data service MUST represent each EntitySet in its associated Entity Data Model as an <app:collection> element. [2.2.6.2.7]' => ["",[1,2,3,4]],
        ];
    }

    public function HeadersTest($field, $Regex, $searchString, $odataVersion)
    {
        $response = $this->getServiceDocument($odataVersion);
        $fieldValue = $response->headers->get($field);
        if (null != $searchString) {
            $this->assertTrue(
                str_contains($fieldValue, $searchString),
                "could not locate search string: " . $searchString . " within Field: ". $field
            );
        }
        $this->assertEquals(
            1,
            preg_match($Regex, $fieldValue),
            "Field: " . $field .' had value: ' . $fieldValue . " which is not matched by regex: " . $Regex
        );
    }

    /**
     * @dataProvider RegExHeaderRulesProvider
     */
    public function testHeaders($field, $Regex, $searchString, $odataVersion)
    {
        foreach ($odataVersion as $version) {
            if (3 != $version) {
                continue;
            }

            $this->HeadersTest($field, $Regex, $searchString, $version);
        }

    }
    public function RegExHeaderRulesProvider()
    {
        return [
            'AtomPub Service Documents MUST be identified with the \'application/atomsvc+xml\' Content Type. [2.2.3.7.1]' => ["Content-Type", "/.*application\/atomsvc\+xml.*/", "application/atomsvc+xml" ,[1,2,3,4]]
        ];
    }
}
